import React from 'react';
import '../assets/css/main.css';
import '../assets/css/bootstrap.min.css';
import Header from '../component/header';
import axios from 'axios';


class Home extends React.Component {
  state = { 
    username: "", 
    password: "" };

  changeInput = e => {
    console.warn("cek event target", e.target.value);

    this.setState({ [e.target.name]: e.target.value });
  };

  postLogin = () => {
    const { username, password } = this.state;
    const data = {
      username: username,
      password: password
    };
    const self = this;
    axios
      .post("https://atareact.free.beeceptor.com/auth", data)
      .then(function (response) {
        console.log(response.data);
        if (response.data.hasOwnProperty("api_key")) {
          localStorage.setItem("full_name", response.data.full_name);
          localStorage.setItem("email", response.data.email);
          localStorage.setItem("api_key", response.data.api_key);
          localStorage.setItem("is_login", true);
          self.props.history.push("/profile");
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  render() {
      // Axios
      //   .post


      return (
        <div className="body">
          <Header/>
          <div className="container" style={{textAlign:"center",paddingTop:"100px"}}>
            <div className="col-md-4"></div>
            <div className="col-md-4" style={{border:"1px solid",paddingTop:"30px",paddingBottom:"30px", borderRadius:"20%"}}>
              <h1>Sign In</h1>
              <input type="text" onChange = {e=> this.changeInput(e)} placeholder="username"/><br/><br/>
              <input type="text" onChange = {e=> this.changeInput(e)} placeholder="password"/><br/><br/>
              <button onClick={() => this.postLogin }>Sign In</button>
            </div>
            <div className="col-md-4"></div>
          </div>
        </div>
      );
    }
  }

  export default Home;
