import createStore from "unistore";
import axios from "axios";

// News API
const apiKey = "619529381a494be9af64c6269526d196";
const baseUrl = "https://newsapi.org/v2/";
// const urlHeadline = baseUrl + "top-headlines?country=id&apiKey=" + apiKey;

const initialState = {
    search : "",
    listDetailBerita:[],
    listBeritaTerkini:[],
    isLoadingBeritaTerkini: true,
    isLoadingDetailBerita: true,
    category:"",
    inputPage1:"",
    stateToChangeFromPage: "belum berubah"
};

export const store = createStore(initialState);

export const actions = store => ({

    handleSearch : (state,e) => {
        console.warn("cek e pada handle input placeholder", e.target.placeholder)
        console.warn("cek value name", e.target.name)
        let value = e.target.value;
        store.setState({ [e.target.placeholder]:value, [e.target.name]:value});
        console.log("cek state input cek", store.getState.category)
        this.getDetailBerita();
        this.getListberitaterkini();

    },

    handleClickKategori : async (e) => {
        console.warn("cek e pada handle input change klik kategori", e.target)
        console.warn("cek value klik kategori", e)
        let value = e;
        await store.setState({ search:value, category:value});
        console.log("cek state input cek", this.state.search);
        this.getDetailBerita();
        this.getListberitaterkini();

    },

    getDetailBerita : () =>{
        const self = this;
        axios
            .get(`${baseUrl}everything?q=${this.state.search}&apiKey=${apiKey}`)
            .then(function(response){
                store.setState({ listDetailBerita: response.data.articles, isLoadingDetailBerita: false});
                console.log("cek dari NEWS",self.state.listDetailBerita);
                // handle success
                // console.log(response.data);
            })
            .catch(function(error){
                store.setState({ isLoadingDetailBerita: false});
                // handle error
                // console.log(error)
            });
    },

    getOut : () => {
        localStorage.removeItem("statusLogin");
        // localStorage.clear()
        this.props.history.push("/");
    },

    goBackHome : () => {
        store.setState({isLoadingBeritaTerkini : true});
    },

    getListberitaterkini : () =>{

    axios
        .get(`${baseUrl}top-headlines?country=id&category=${this.state.category}&apiKey=${apiKey}`)
        .then(function(response){
        store.setState({ listBeritaTerkini: response.data.articles, isLoadingBeritaTerkini: false});
        // handle success
        // console.log(response.data);
        })
        .catch(function(error){
        store.setState({ isLoadingBeritaTerkini: false});
        // handle error
        // console.log(error)
        });
    }


})
