import React from 'react';
import axios from "axios";
import '../assets/css/main.css';
import '../assets/css/bootstrap.min.css';
import Header from '../component/header';
import BeritaTerkini from '../component/beritaTerkini';
import DetailBerita from '../component/detail_berita';

// News API
const apiKey = "619529381a494be9af64c6269526d196";
const baseUrl = "https://newsapi.org/v2/";
// const urlHeadline = baseUrl + "top-headlines?country=id&apiKey=" + apiKey;

class Home extends React.Component {
  state = {
    listNews:[],
    isLoading: true,
    category: "sport"
  };

  componentDidMount = () =>{
    const self = this;
    axios
      .get(`${baseUrl}top-headlines?country=id&category=${this.state.category}&apiKey=${apiKey}`)
      .then(function(response){
        self.setState({ listNews: response.data.articles, isLoading: false});
        // handle success
        // console.log(response.data);
      })
      .catch(function(error){
        self.setState({ isLoading: false});
        // handle error
        // console.log(error)
      });
  };

  render() {
    // const news = this.state.listNews;
    const { listNews, isLoading } = this.state;
    // console.log("news", news);
    const topHeadlines = listNews.filter(item => {
      if (item.content !== null && item.image !== null) {
        return item;
      }
      return false;
    })

    const headlineNews = topHeadlines.map((item, key)=>{
      return (
        <div className="body">
          <Header/>
          <div className="container">
            <div className="row">
            <BeritaTerkini 
              judul={item.title}
            />
            <DetailBerita/>
            </div>
          </div>
        </div>
      );
    })

    return (
      <div className="headlinesNews">
        {isLoading ? <div style={{textAlign:"center"}}>Mohon Tunggu</div> : headlineNews}
      </div>
    )

  }
}

  export default Home;
