import React from 'react';
import { Redirect} from 'react-router-dom';
import '../assets/css/main.css';
import '../assets/css/bootstrap.min.css';
import Header from '../component/header';

import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store";

getApiResponse = () =>{
  axios
      .post("https://react-challenge.free.beeceptor.com/")
      .then(function(response){
          store.setState({ name: response.data.name});
          console.log("cek state name", props.name);
          store.setState({ email: response.data.email});
          console.log("cek state name", props.email);
          store.setState({ api_key: response.data.api_key});
          console.log("cek state name", props.api_key);
          // handle success
          // console.log(response.data);
      })
      .catch(function(error){
          store.setState({ isLoadingDetailBerita: false});
          // handle error
          // console.log(error)
      });
}



const Profile = props => {
  this.getApiResponse()
  // const statusLogin = JSON.parse(localStorage.getItem("statusLogin"));
  console.log("cek state statusLogin", props.statusLogin);
  // const name = localStorage.getItem("name");
  // const email = localStorage.getItem("email");
  console.log("cek state statusLogin", props.statusLogin);
  if (props.statusLogin === false) {
    return <Redirect to={{ pathname: "/signin" }} />;
  } else {
    return (
      <React.Fragment>
        <Header {...props} />
        <section className="content" style={{paddingTop:"100px",textAlign:"center"}}>
          <h1
            style={{
              textalign: "center"
            }}
          >
            Profile
          </h1>
          <p>
            <label>Name:</label> name
          </p>
          <p>
            <label>Email:</label> email
          </p>
        </section>
      </React.Fragment>
    );
  }
};

export default connect("search, name, email, api_key, statusLogin", actions)(withRouter(Profile));
